'use strict';

var _ = require('lodash-node');

/**
 * @param {Function} Parent
 * @param {Object} [prototype]
 * @param {Object} [statics]
 *
 * @returns {Function}
 * */
module.exports = function (Parent, prototype, statics) {

    prototype = _.extend(Object.create(Parent.prototype), {
        constructor: function () {
            Parent.apply(this, arguments);
        }
    }, prototype);

    return _.extend(prototype.constructor, {
        Parent: Parent,
        parent: Parent.prototype,
        prototype: prototype
    }, statics);
};
